"use strict";Vue.component("v-modal",{template:'\n    <transition :name="transitionName">\n      <div class="v-modal" v-if="visible" :style="boxStyle">\n        <div class="top-header">{{label}}</div>\n        <div class="content" style="height:100%;padding: 40px 5px 40px 20px;">\n          <b-scrollbar style="height: 100%;" always>\n            <slot></slot>\n          </b-scrollbar>\n        </div>\n        <div class="angle top-left"></div>\n        <div class="angle top-right"></div>\n        <div class="angle bottom-left"></div>\n        <div class="angle bottom-right"></div>\n        <i class="iconfont icon-ios-close" @click="onClose"></i>\n      </div>\n    </transition>',props:{label:{type:String,default:"主标题"},transitionName:{type:String,default:"zoom-in-bottom"},rect:{type:Array,default:function(){return[40,20,460,462,1e3]}},value:Boolean},data:function(){return{visible:!1}},created:function(){this.visible=this.value},watch:{value:function(t){this.visible=t,this.$emit("input",t)}},computed:{boxStyle:function(){return{position:"absolute",left:this.rect[0]+"px",top:this.rect[1]+"px",width:this.rect[2]+"px",height:this.rect[3]+"px",zIndex:this.rect[4]?this.rect[4]:1e3}}},methods:{onClose:function(){this.visible=!1,this.$emit("input",this.visible),this.$emit("on-close")}}});